CREATE TABLE IF NOT EXISTS TASKS(id INTEGER PRIMARY KEY AUTOINCREMENT,name TEXT NOT NULL,description TEXT,starting_date DATE NOT NULL,starting_time TIME NOT NULL,duration INTEGER,duration_unit TEXT CHECK(duration_unit IN ('hrs','mins')) ,priority INTEGER NOT NULL CHECK(priority BETWEEN 0 AND 6),completed_date DATE, completed_time TEXT,is_cancelled INTEGER CHECK(is_cancelled IN (1,0)));